import React, {useEffect, useState} from 'react'

const BreadCrumb = (props) => {

    const [showBreadcrumb, setShowBreadCrumb] = useState([])


    useEffect(() => {

        let 
        url = window.location.href,
        urlArr = url.split('/')
        
        setShowBreadCrumb(urlArr)

    }, [])


    return (
        <div className="section breadcrumb">        
            <div className="container-fluid wrapper-fluid">
                <div className="list">

                    {showBreadcrumb[0] && showBreadcrumb[1] && showBreadcrumb[2] &&
                        <Link href="/">
                            <a>Home</a>
                        </Link>
                    }

                    {showBreadcrumb[3] &&
                        <Link href={showBreadcrumb[3]}>
                            <a>{showBreadcrumb[3]}</a>
                        </Link>
                    }                    
                </div>
            </div>
        </div>
    )
}

export default BreadCrumb
