                                        ref={register({
                                            required: "Este campo es obligatorio"
                                        })}
                                        />
                                        {errors.con_fecha_entrega && <span className="fi-validator">{errors.con_fecha_entrega.message}</span>}
                                    </div>

                                    <div className="box-form">
